//RETTANGOLO
wire here,here1,here2;
assign here = here1 || here2;
wire in,in1,in2;
assign in = in1 || in2;

parameter altezza = 500;
parameter larghezza = 300;

reg [10:0] posx = (H/2 - larghezza/2);
reg [10:0] posy = (V/2 - altezza/2);


wire [10:0] differenza = H - posx;

cornicetta #(
.altezza (altezza),
.larghezza (larghezza)
) RET(
.X_POS (posx),
.Y_POS (posy),
//Controllo
.X_CONTROLLO (x),
.Y_CONTROLLO (y),

.CONFERMA (here1),
.interno(in1)

);



cornicetta #(
.altezza (altezza),
.larghezza (larghezza)
) RET2(
.X_POS (posx + H),
.Y_POS (posy + V),
//Controllo
.X_CONTROLLO (x),
.Y_CONTROLLO (y),

.CONFERMA (here2),
.interno(in2)

);


//SCHERMO
always@(posedge VGA_CLK)
begin
	if(disp_en) 
	begin
		if (here)
		begin
		r <= full;
		g <= empty;
		b <= empty;
		end
		else if(in) begin
		r <= empty;
		g <= full;
		b <= empty;
		end else
		begin
		r <= empty;
		g <= empty;
		b <= full;
		end
	end
end


always@(posedge VGA_VS or negedge reset)
begin
	if (!reset)
	begin
	posx = (H/2 - larghezza/2);
	posy = (V/2 - altezza/2);
	end
	else
	begin
		if (!KEY[1])
		begin
			if (SW[0])
			begin
				if (posx == H)
				posx <= 10'd0;
				else
				posx <= posx + 10'd1;
			end
			else
			begin
				if (posx == (10'd0))
				posx <= H ;
				else
				posx <= posx - 10'd1;
			end
		end
		else
			posx <= posx;
	
		if (!KEY[2])
		begin
			if (SW[1])
			begin
				if (posy == V)
				posy <= 10'd0;
				else
				posy <= posy + 10'd1;
			end
			else
			begin
				if (posy == (10'd0))
				posy <= V ;
				else
				posy <= posy - 10'd1;
			end
		end
		else
		posy <= posy;

	end
end
