module VGA_Try0(

	//////////// CLOCK //////////
	input 		          		CLOCK_50,
	input 		          		CLOCK2_50,
	input 		          		CLOCK3_50,
	input 		          		CLOCK4_50,

	//////////// SEG7 //////////
	output		     [6:0]		HEX0,
	output		     [6:0]		HEX1,
	output		     [6:0]		HEX2,
	output		     [6:0]		HEX3,
	output		     [6:0]		HEX4,
	output		     [6:0]		HEX5,

	//////////// KEY //////////
	input 		     [3:0]		KEY,

	//////////// LED //////////
	output		     [9:0]		LEDR,

	//////////// SW //////////
	input 		     [9:0]		SW,

	//////////// VGA //////////
	output		     [7:0]		VGA_B,
	output		          		VGA_BLANK_N,
	output		          		VGA_CLK,
	output		     [7:0]		VGA_G,
	output		          		VGA_HS,
	output		     [7:0]		VGA_R,
	output		          		VGA_SYNC_N,
	output		          		VGA_VS,
	

	//////////// GPIO_1, GPIO_1 connect to GPIO Default //////////
	inout 		    [35:0]		GPIO1GPIO
	);



//=======================================================
//  REG/wIRE declarations
//=======================================================

wire disp_en;
wire [31:0] X;
wire [31:0] Y;
wire reset;

reg [7:0] r,g,b;

parameter H = 1280;
parameter V = 1024;
parameter full = 8'hff;
parameter empty = 8'b0;

timing tm(
.clk (VGA_CLK)		,
.rst_n	(reset)	,
//direttamente alla porta
.hsync	(VGA_HS),
.vsync	(VGA_VS),
//al ADV7123
//.blank_n	(VGA_BLANK_N),
.sync_n	(VGA_SYNC_N),
//al mux per i pixels
.disp_enable  (disp_en),
.Xpix (X),
.Ypix (Y)
);


//=======================================================
//  Structural coding
//=======================================================
assign reset = KEY[0];
//assign VGA_CLK = CLOCK4_50;
assign VGA_BLANK_N = SW[0];
assign LEDR[1] = VGA_BLANK_N ;
assign VGA_R = (disp_en)?r:empty;
assign VGA_G = (disp_en)?g:empty;
assign VGA_B = (disp_en)?b:empty;

assign GPIO1GPIO[1:0]={disp_en,VGA_CLK};

PLL_0002 pll(	

.refclk (CLOCK_50),

	// interface 'reset'
.rst (~reset),

	// interface 'outclk0'
.outclk_0 (VGA_CLK),

	// interface 'locked'
.locked (LEDR[9])
	);

initial
begin
r <= empty;
b <= empty;
g <= empty;
end

always@(posedge VGA_CLK)
begin
 if(disp_en) begin
		if (X < 21)begin
			r <= full;
			b <= full;
			g <= full;
		end
		else if (Y < 21)begin
			r <= empty;
			b <= full;
			g <= full;
		end
		else if (X > H-21)begin
			r <= full;
			b <= full;
			g <= empty;
		end
		else if (Y > V-21)begin
			r <= full;
			b <= empty;
			g <= full;
		end
		else begin
			r <= empty;
			b <= full-Y;
			g <= empty;
		end
	end
	else begin
r <= empty;
b <= empty;
g <= empty;
end
end
